package payilagam;

public class Test2Pattern1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for (int i=1;i<=7;i++)
		{
			for (int j=1;j<=i;j++)
			{
				System.out.print(j + " "); //Using just 'print' instead of println to cursor in same line while printing
			}
			System.out.println();
		}
	}

}
